mapper.insert:
insert into block_tran_change (id, height, tran_number
  )
values (#{id,jdbcType=INTEGER}, #{height,jdbcType=INTEGER}, #{tran_number,jdbcType=INTEGER}
  )

mapper.update:
update block_tran_change
set height = #{height,jdbcType=INTEGER},
  tran_number = #{tran_number,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, height, tran_number
from block_tran_change
where id = #{id,jdbcType=INTEGER}
