mapper.update:
update person_message
set uname = #{uname,jdbcType=VARCHAR},
  message = #{message,jdbcType=VARCHAR},
  messagetype = #{messagetype,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, uname, message, messagetype
from person_message
where id = #{id,jdbcType=INTEGER}
